import '../index.css';
import api from "../utils/api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
import AddPlacePopup from "./AddPlacePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import DeleteCardPopup from "./DeleteCardPopup";
import InfoTooltip from "./InfoTooltip";
import Login from "./Login";
import Register from "./Register";
import ProtectedRoute from "./ProtectedRoute";
import {Route, Switch, useHistory} from "react-router-dom";
import * as auth from '../utils/auth';


function App() {

    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = React.useState(false);

    const [cards, setCards] = React.useState([]);
    const [currentUser, setCurrentUser] = React.useState({});
    const [selectedCard, setSelectedCard] = React.useState({link: "", name: "", isOpen: false});
    const [selectedCardDelete, setSelectedCardDelete] = React.useState({});

    //стейт логина пользователя
    const [loggedIn, setLoggedIn] = React.useState(false)

    //cтейт окна инфопопапа
    const [infoPopupOpen, setInfoPopupOpen] = React.useState(false);

    //стейт отображения ошибки в инфопопе
    const [isSuccess, setIsSuccess] = React.useState(false)

    //user-email header show
    const [email, setEmail] = React.useState('')


    const history = useHistory();

    React.useEffect(() => {
        api.getInitialCards()
            .then((cards) => {
                setCards(cards);
            })
            .catch(err => console.log(err));
    }, []);

    React.useEffect(() => {
        api.getUserInfo()
            .then(data => {
                setCurrentUser(data)})
            .catch(err => console.log(err));
    }, []);

    function handleCardClick(card) {
        setSelectedCard({link: card.link, name: card.name, isOpen: true});
    }
    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
    }

    function handleDeleteCardClick(card) {
        setIsDeleteCardPopupOpen(!isDeleteCardPopupOpen);
        setSelectedCardDelete(card);
    }

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        if (!isLiked) {
            api.putLike(card._id)
                .then((newCard) => {
                    const newCards = cards.map((c) => c._id === card._id ? newCard : c)
                    setCards(newCards)
                })
                .catch(err => console.log(err));

        } else {
            api.deleteLike(card._id)
                .then((newCard) => {
                    const newCards = cards.map((c) => c._id === card._id ? newCard : c)
                    setCards(newCards)
                })
                .catch(err => console.log(err));
        }
    }
    function handleCardDelete() {
        api.deleteCard(selectedCardDelete._id)
            .then(() => {
                const newCards = cards.filter((c) => c._id !== selectedCardDelete._id);
                setCards(newCards);
                setSelectedCardDelete({});
                closeAllPopups();
            })
            .catch(err => console.log(err));
    }
        function handleUpdateUser(data) {
            api.patchUserProfile(data)
                .then(() => {
                    setCurrentUser({...currentUser, ...data});
                    closeAllPopups();
                })
                .catch(err => console.log(err));
        }

    function handleUpdateAvatar(data) {
        api.patchAvatar(data)
            .then(res => {
                setCurrentUser(res);
            })
            .then(() => {
                setIsEditAvatarPopupOpen(false);
            })
            .catch(err => console.log(err));
    }

        function handleAddPlaceSubmit(card) {
            api.postUserCard(card)
                .then((newCard) => {
                    setCards([newCard, ...cards]);
                    closeAllPopups();
                })
                .catch(err => console.log(err));
        }
    function closeAllPopups() {
        setIsEditProfilePopupOpen(false)
        setIsEditAvatarPopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsDeleteCardPopupOpen(false)
        setSelectedCard({link: "", name: "", isOpen: false});
    };


    const handleRegisterConfirm = (foo) => {
        setInfoPopupOpen(true)
        setIsSuccess(foo);
    }

    const handleRegister = (email, password) => {
        auth.register(email, password)
            .then((res) => {
                if (res) {
                    handleRegisterConfirm(true)
                    history.push('./sign-in')
                } else {
                    handleRegisterConfirm(false);
                }
            })
            .catch((err) => {
                console.log(err.message)
            })
    }

    const handleLogin = (email, password) => {
        auth.authorize(email, password)
            .then(data => {
                if (data.token) {
                    setEmail(email)
                    setLoggedIn(true)
                    history.push('/')
                }
            })
            .catch(err => console.log(err.name, err.message))
    }

    const onSignOut = () => {
        localStorage.removeItem('jwt');
        history.push('./login');
        setEmail('')
        setLoggedIn(false)
    }

    const tokenCheck = () => {
        const jwt = localStorage.getItem('jwt');
        if (jwt) {
            auth.getContent(jwt)
                .then((res) => {
                    if (res) {
                        setEmail(res.data.email);
                        setLoggedIn(true);
                        history.push('./');
                    }
                })
                .catch(err => {
                    console.log(err);
                    history.push('./sign-in');
                });
        }
    }

    React.useEffect(() => {
        tokenCheck()
    }, [])

  return (
      <CurrentUserContext.Provider value={currentUser}>
    <div className="page">
      <Header />
        <Main
            onEditProfile={handleEditProfileClick}
            onEditAvatar={handleEditAvatarClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            onCardLike = {handleCardLike}
            onCardDelete = {handleDeleteCardClick}
            cards = {cards}
            />
      <Footer />
       <ImagePopup
            name="img"
            card={selectedCard}
            onClose={closeAllPopups}/>
        <AddPlacePopup
            onAddPlace={handleAddPlaceSubmit}
            onClose={closeAllPopups}
            isOpen={isAddPlacePopupOpen}
            />
        <EditAvatarPopup
            onUpdateAvatar={handleUpdateAvatar}
            onClose={closeAllPopups}
            isOpen={isEditAvatarPopupOpen}/>
        <EditProfilePopup
            onUpdateUser={handleUpdateUser}
            onClose={closeAllPopups}
            isOpen={isEditProfilePopupOpen}/>
       <DeleteCardPopup
            onClose={closeAllPopups}
            isOpen={isDeleteCardPopupOpen}
            onDeleteCard={handleCardDelete}/>
    </div>
      </CurrentUserContext.Provider>
  );
}

export default App;
